import React, { useContext } from "react";
import { useParams, useHistory } from "react-router-dom";
import { ByteContext } from "../context/bytes";
import { CartContext } from "../context/cart";

const ByteDetails = () => {
  const { id } = useParams();
  const history = useHistory();
  const { bytes } = useContext(ByteContext);
  const { addToCart } = useContext(CartContext);

  const byte = bytes.find((byte) => {
    return byte.id === id;
  });
  if (!byte) {
    return <h3>Loading...</h3>;
  }

  const { index, size, price } = byte;

  return (
    <section className="byte-details">
      <div className="detail-description">
        <h2>Bits {index} to {index + size}</h2>
        <h4>Price - GBP {price}</h4>
        <button
          className="btn"
          onClick={() => {
            addToCart({ ...byte, id });
            history.push("/cart");
          }}
        >
          Add to Cart
        </button>
      </div>
    </section>
  );
};

export default ByteDetails;
