import React, { useContext } from 'react'
import { Link } from "react-router-dom";
import { ByteContext } from '../context/bytes';
import Box from '@material-ui/core/Box';

const Bytes = () => {
    const { bytes } = useContext(ByteContext);

    if (!bytes.length) {
        return <h3>No Bytes Available</h3>
    }

    return (
        <section className="bytes">
            {bytes.map(({ id, index, owner, content }) => (
              <div className="row">
                <article key={id} className="byte">
                    {owner === null &&
                      <Box component="span" m={1}>
                        <div className="leftDiv">{index}</div>
                        <div className="rightDiv">
                          <Link to={`${id}`} className="btn byte-link">buy</Link>
                        </div>
                      </Box>
                    }
                    {owner !== null &&
                      <Box component="span" m={1}>
                        <div className="leftDiv">{index}</div>
                        <div className="rightDiv">{content}</div>
                      </Box>
                    }
                </article></div>
            ))}
        </section>
    )
}

export default Bytes
